	private model = Products;

	async findAll(): Promise<IProducts[]> {
		const data = await this.model.findAll({
			attributes: ['id', 'productName', 'description', 'price', 'illustration'],
			include: [
				{
					model: Restaurants,
					attributes: ['id', 'restaurantName'],
					as: 'restaurant'
				},
				{
