					model: Restaurants,
					attributes: ['id', 'restaurantName'],
					as: 'restaurant'
				},
				{
					model: ProductType,
					attributes: ['id', 'type'],
					as: 'product_types',
					where: {
						id: id,
					}
				}
			]
		});
		return data;
	}

}

